package aop.proxy;import aop.Action;import aop.impl.PureImpl;/** * Created with IntelliJ IDEA. * Project: test-jar * Author: Kevin * Date: 2018/7/1 * Time: 下午3:45 */public class OneActionOneProxy implements Action {	private Action target = new PureImpl();	@Override	public <T> void add(T t) {		before();		target.add(t);		after();	}	private void before() {		System.out.println("before");	}	private void after() {		System.out.println("after");	}	public Action getAction() {		return target;	}	public void setAction(Action action) {		this.target = action;	}}